import React from 'react'
import styled from 'styled-components'

/* Colors */
/*
grey: #818D92
beige: #ECEBE4
reddish: #C49792
blackish: #2B303A
grey green: #586A6A
*/

const Styles = styled.div`
.wrapper {
  height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: #C49792;
}

.form-wrapper {
  width: 400px;
  display: flex;
  flex-direction: column;
  padding: 20px 40px;
  border-radius: 10px;
  box-shadow: 0px 10px 50px #555;
  background-color: #ffffff;
}

form {
  width: 100%;
  display: flex;
  flex-wrap: wrap;
}

h1 {
  text-align: center;
  width: 100%;
  margin-bottom: 2vh; 
  color: #111;
  font-weight: lighter;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen,
    Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
}

label {
  font-size: 0.8em;
  margin-bottom: 0.25em;
  color: #222;
  font-weight: lighter;
}

input {
  padding: 10px 10px;
  border-radius: 5px;
  outline: none;
  border: 1px solid #cfcfcf;
}

input::placeholder {
  font-size: 1.2em;
  font-weight: lighter;
  color: #999;
}

input.error {
  border: 1px solid red;
}

.errorMessage {
  color: red;
  font-size: 0.75em;
  display: relative;
}

.firstName {
  margin-right: 1%;
}

.lastName {
  margin-left: 1%;
}

.firstName,
.lastName,
.email,
.password {
  display: flex;
  flex-direction: column;
  margin-bottom: 15px;
}

.firstName,
.lastName {
  width: 49%;
}

.email,
.password {
  width: 100%;
}

.createAccount {
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.createAccount button {
  background-color: #519e8a;
  color: #fff;
  border: 2px solid #fff;
  width: 100%;
  margin-top: 1em;
  padding: 8px 0px;
  font-size: 1em;
  font-weight: lighter;
  letter-spacing: 1px;
  margin-bottom: 0.25em;
}

.createAccount button:hover {
  color: #519e8a;
  background-color: #fff;
  border: 2px solid #519e8a;
}

.createAccount small {
  color: #999;
  font-weight: lighter;
}
`
const formValid = (formErrors) => {
    let valid = true

    Object.values(formErrors).forEach(val => {
        val.length > 0 && (valid = false)
    })

    return valid
}

const emailRegex = RegExp(
    /^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
  )

export const SignUp = () => {
    const state = {
        firstName: null,
        lastName: null,
        email: null,
        password: null,
        formErrors: {
            firstName: "",
            lastName: "",
            email: "",
            password: ""
        }
    }

    const handleSubmit = event => {
        event.preventDefault()

        if (formValid(state.formErrors)) {
            console.log(`
            --SUBMITTING--
            First Name: ${state.firstName}
            Last Name: ${state.lastName}
            Email: ${state.email}
            Password: ${state.password}
            `)
        } else {
            console.log('FORM INVALID - DISPLAY ERROR MESSAGE');
        }
    }

    const handleChange = event => {
        event.preventDefault()
        const { name, value } = event.target
        let formErrors = state.formErrors

        console.log("Context:", name)
        console.log("Value:", value)

        switch (name) {
            case 'firstName':
                formErrors.firstName = value.length < 2 
                    ? 'minimum 2 characters required'
                    : ""
                break

            case 'lastName':
                formErrors.lastName = value.length < 2
                    ? 'minimum 2 characters required'
                    : ""
                break

            case 'email':
                formErrors.email = emailRegex.test(value)
                    ? ""
                    : 'Invald email address'
                break

            case 'password':
                formErrors.password = value.length < 6
                    ? 'minimum 6 characters required'
                    : ""
                break
            default:
                break
        }

        //this.setState({ formErrors, [name]: value }, () => console.log(state))
    }

    const { formErrors } = state

    return (
        <Styles>
            <div className="wrapper">
                <div className="form-wrapper">
                    <h1>Create Account</h1>
                    <form onSubmit={handleSubmit} noValidate>
                        <div className="firstName">
                            <label htmlFor="firstName">First Name</label>
                            <input 
                                className={formErrors.firstName.length > 0 ? error : null}
                                placeholder="First Name"
                                type="text"
                                name="firstName"
                                noValidate
                                onChange={handleChange}
                            />
                            {formErrors.firstName.length > 0 && (
                                <span className="errorMessage">{formErrors.firstName}</span>
                            )}
                        </div>
                        <div className="lastName">
                            <label htmlFor="lastName">Last Name</label>
                            <input 
                                className=""
                                placeholder="Last Name"
                                type="text"
                                name="lastName"
                                noValidate
                                onChange={handleChange}
                            />
                        </div>
                        <div className="email">
                            <label htmlFor="email">Email</label>
                            <input 
                                className=""
                                placeholder="Email"
                                type="email"
                                name="email"
                                noValidate
                                onChange={handleChange}
                            />
                        </div>
                        <div className="password">
                            <label htmlFor="password">Password</label>
                            <input 
                                className=""
                                placeholder="Password"
                                type="password"
                                name="password"
                                noValidate
                                onChange={handleChange}
                            />
                        </div>
                        <div className="createAccount">
                            <button type="submit">Create Account</button>
                            <small>Already Have an Account?</small>
                        </div>
                    </form>
                </div>
            </div>
        </Styles>
    )
}
