import React, { Component } from 'react';
import { Spring } from 'react-spring/renderprops';
import { Nav, Navbar } from 'react-bootstrap';
import auth from '../components/auth';
import axios from 'axios';
//import userService from '../services/accounts';
import styled from 'styled-components';

const Styles = styled.div`
    .navbar {
        background-color: rgba(0,0,0,0.9);
    }

    .navbar-default, .collapsed {
        border-color: white;
        background-color: white;
      }

      .navbar-default, .toggle{
       background-color: white;
      }

    .navbar-brand, .navbar-nav .nav-link  {
        color white;
        margin: 2vh 2vw 2vh 2vw;

        &:hover {
            color: #690505;
        }
    }

    .nav-item {
      color white;
      margin: 2vh 2vw 2vh 2vw;
    }
    `;

/*let userArray = [
  {
    firstName: 'Benjamin',
    lastName: 'Bow',
    email: 'boy@com',
    password: 'benjambo'
  }
];*/

// Function to validate if input is acceptable
/*const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // Validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // Validate form if it isn't filled out
  Object.values(rest).forEach(val => {
    val === '' && (valid = false);
  });

  return valid;
};*/

// An email validation code
const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

export class SignUp extends Component {
  constructor(props) {
    super(props);

    // The state of the form to begin with
    this.state = {
      //users: userArray,
      firstName: '',
      lastName: '',
      email: '',
      password: ''
      /*formErrors: {
        firstName: '',
        lastName: '',
        email: '',
        password: ''
      }*/
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  // Fetch the user on first mount
  /*componentDidMount() {
    this.getUser();
  }

  // Retrieves the array of users from the Express app
  getUser = () => {
    fetch('/api/getUser')
      .then(res => res.json())
      .then(users => this.setState({ users }));
  };*/

  // The submit handler
  handleSubmit = event => {
    event.preventDefault();
    //const data = new FormData(event.target);

    // If the format is valid print this to Console
    if (this.state) {
      /*console.log(`
            --SUBMITTING--
            First Name: ${this.state.firstName}
            Last Name: ${this.state.lastName}
            Email: ${this.state.email}
            Password: ${this.state.password}
            `);*/

      /*const { users } = this.state;
      this.setState({
        users: [...users]
      });

      users.push({
        firstName: this.state.firstName,
        lastName: this.state.lastName,
        email: this.state.email,
        password: this.state.password
      });*/

      /*fetch('/signup', {
        method: 'POST',
        body: 
      });*/

      axios
        .post('/signup', this.state)
        .then(res => {
          console.log(res);
        })
        .catch(err => {
          console.log(err);
        });

      console.log(this.state);
      /*userService
        .create({
          firstName: this.state.firstName,
          lastName: this.state.lastName,
          email: this.state.email,
          password: this.state.password
        })
        .then(createdUser => {
          users.concat(createdUser);
        });*/

      /*auth.login(() => {
        this.props.history.push('/home');
      });*/
    } else {
      console.log('INVALID FORM - ERROR MESSAGE');
    }
  };

  // The Change handler
  handleChange = event => {
    // Swith statement for form input rules
    /*switch (name) {
      case 'firstName':
        formErrors.firstName =
          value.length < 2 ? 'minimum 2 characters required' : '';
        break;

      case 'lastName':
        formErrors.lastName =
          value.length < 2 ? 'minimum 2 characters required' : '';
        break;

      case 'email':
        formErrors.email = emailRegex.test(value) ? '' : 'Invald email address';
        break;

      case 'password':
        formErrors.password =
          value.length < 6 ? 'minimum 6 characters required' : '';
        break;
      default:
        break;
    }*/

    this.setState({ [event.target.name]: event.target.value });
    /*, () => console.log(this.state));*/
  };

  render() {
    // Initializing formErrors property for it to be able to be used
    const { firstName, lastName, email, password /*formErrors*/ } = this.state;
    //const { users } = this.state;

    return (
      <form action="/signup" className="forms" onSubmit={this.handleSubmit}>
        <div className="firstName">
          <label htmlFor="firstName">First Name</label>
          <input
            placeholder="First Name"
            type="text"
            name="firstName"
            value={firstName}
            onChange={this.handleChange}
          />
          )}
        </div>
        <div className="lastName">
          <label htmlFor="lastName">Last Name</label>
          <input
            placeholder="Last Name"
            type="text"
            name="lastName"
            value={lastName}
            onChange={this.handleChange}
          />
          )}
        </div>
        <div className="email">
          <label htmlFor="email">Email</label>
          <input
            placeholder="Email"
            type="email"
            name="email"
            value={email}
            onChange={this.handleChange}
          />
        </div>
        <div className="password">
          <label htmlFor="password">Password</label>
          <input
            placeholder="Password"
            type="password"
            name="password"
            value={password}
            onChange={this.handleChange}
          />
        </div>
        <div className="createAccount">
          <button type="submit">Create Account</button>
          <a href="/">
            <small>Already Have an Account?</small>
          </a>
        </div>
      </form>
    );
  }
}
