import React, { useState, useEffect } from 'react';
import { Spring } from 'react-spring/renderprops';
import { Nav, Navbar } from 'react-bootstrap';
import auth from '../components/auth';
import userService from '../services/accounts';
import styled from 'styled-components';

const Styles = styled.div`
    .navbar {
        background-color: rgba(0,0,0,0.9);
    }

    .navbar-default, .collapsed {
        border-color: white;
        background-color: white;
      }

      .navbar-default, .toggle{
       background-color: white;
      }

    .navbar-brand, .navbar-nav .nav-link  {
        color white;
        margin: 2vh 2vw 2vh 2vw;

        &:hover {
            color: #690505;
        }
    }

    .nav-item {
      color white;
      margin: 2vh 2vw 2vh 2vw;
    }
    `;

let users = [
  {
    firstName: 'Benjamin',
    lastName: 'Bow',
    email: 'boy@com',
    password: 'benjambo'
  }
];

// Function to validate if input is acceptable
const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // Validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // Validate form if it isn't filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });

  return valid;
};

// An email validation code
const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

export const SignUp = () => {
  const [users, setUsers] = useState([]);
  const [fname, setFname] = useState('');
  const [lname, setLname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    userService.getAll().then(data => {
      setUsers(data);
    });
  }, []);

  // The submit handler
  const handleSubmit = event => {
    event.preventDefault();

    // If the format is valid print this to Console
    if (formValid(this.state)) {
      console.log(`
            --SUBMITTING--
            First Name: ${this.state.firstName}
            Last Name: ${this.state.lastName}
            Email: ${this.state.email}
            Password: ${this.state.password}
            `);

      userService
        .create({
          firstName: this.state.firstName,
          lastName: this.state.lastName,
          email: this.state.email,
          password: this.state.password
        })
        .then(createdUser => {
          users.concat(createdUser);
        });

      auth.login(() => {
        this.props.history.push('/home');
      });
    } else {
      console.log('INVALID FORM - ERROR MESSAGE');
    }
    return;
  };

  // The Change handler
  const handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;
    let formErrors = this.state.formErrors;

    // Swith statement for form input rules
    switch (name) {
      case 'firstName':
        formErrors.firstName =
          value.length < 2 ? 'minimum 2 characters required' : '';
        break;

      case 'lastName':
        formErrors.lastName =
          value.length < 2 ? 'minimum 2 characters required' : '';
        break;

      case 'email':
        formErrors.email = emailRegex.test(value) ? '' : 'Invald email address';
        break;

      case 'password':
        formErrors.password =
          value.length < 6 ? 'minimum 6 characters required' : '';
        break;
      default:
        break;
    }
  };

  setUsers({ formErrors, [name]: value }, () => console.log(this.state));

  return (
    <Styles>
      <Navbar expand="lg" fixed="top">
        <Navbar.Brand>Perfiction</Navbar.Brand>
        <Navbar.Toggle area-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
            <Nav.Item>Welcome to Perfiction</Nav.Item>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      <Spring
        from={{ opacity: 0 }}
        to={{ opacity: 1 }}
        config={{ duration: 1250 }}
      >
        {props => (
          <div style={props} className="wrapper">
            <div className="form-wrapper">
              <h1>Create Account</h1>
              <form className="forms" onSubmit={handleSubmit} noValidate>
                <div className="firstName">
                  <label htmlFor="firstName">First Name</label>
                  <input
                    className={formErrors.firstName.length > 0 ? 'error' : null}
                    placeholder="First Name"
                    type="text"
                    name="firstName"
                    noValidate
                    onChange={handleChange}
                  />
                  {formErrors.firstName.length > 0 && (
                    <span className="errorMessage">{formErrors.firstName}</span>
                  )}
                </div>
                <div className="lastName">
                  <label htmlFor="lastName">Last Name</label>
                  <input
                    className=""
                    placeholder="Last Name"
                    type="text"
                    name="lastName"
                    noValidate
                    onChange={handleChange}
                  />
                  {formErrors.lastName.length > 0 && (
                    <span className="errorMessage">{formErrors.lastName}</span>
                  )}
                </div>
                <div className="email">
                  <label htmlFor="email">Email</label>
                  <input
                    className=""
                    placeholder="Email"
                    type="email"
                    name="email"
                    noValidate
                    onChange={handleChange}
                  />
                  {formErrors.email.length > 0 && (
                    <span className="errorMessage">{formErrors.email}</span>
                  )}
                </div>
                <div className="password">
                  <label htmlFor="password">Password</label>
                  <input
                    className=""
                    placeholder="Password"
                    type="password"
                    name="password"
                    noValidate
                    onChange={handleChange}
                  />
                  {formErrors.password.length > 0 && (
                    <span className="errorMessage">{formErrors.password}</span>
                  )}
                </div>
                <div className="createAccount">
                  <button type="submit">Create Account</button>
                  <a href="/">
                    <small>Already Have an Account?</small>
                  </a>
                </div>
              </form>
            </div>
          </div>
        )}
      </Spring>
    </Styles>
  );
};

